import React, { useState, useEffect, useRef } from "react";
import { db, auth } from "../firebase-config";
import {
  collection,
  addDoc,
  where,
  serverTimestamp,
  onSnapshot,
  query,
  orderBy,
  setDoc,
  doc,
  updateDoc,
  deleteDoc,
} from "firebase/firestore";

import EmojiPicker from "emoji-picker-react";
import "../styles/Chat.css";

const reactionOptions = ["ðŸ‘", "â¤ï¸", "ðŸ˜‚", "ðŸ˜®", "ðŸ˜¢"];

export const Chat = ({ room }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [typingUsers, setTypingUsers] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const typingRef = collection(db, "typing");
  const messagesRef = collection(db, "messages");
  const typingTimeout = useRef(null);
  const inputRef = useRef(null);

  useEffect(() => {
    const queryMessages = query(
      messagesRef,
      where("room", "==", room),
      orderBy("createdAt")
    );

    const unsubscribe = onSnapshot(queryMessages, (snapshot) => {
      const msgs = [];
      snapshot.forEach((doc) => {
        msgs.push({ ...doc.data(), id: doc.id });
      });
      setMessages(msgs);
    });

    const typingQuery = query(typingRef, where("room", "==", room));
    const unsubscribeTyping = onSnapshot(typingQuery, (snapshot) => {
      const usersTyping = [];
      snapshot.forEach((doc) => {
        if (doc.id !== auth.currentUser.uid) {
          usersTyping.push(doc.data().user);
        }
      });
      setTypingUsers(usersTyping);
    });

    return () => {
      unsubscribe();
      unsubscribeTyping();
    };
  }, [room]);

  const handleTyping = async (value) => {
    setNewMessage(value);
    const currentUser = auth.currentUser;

    if (typingTimeout.current) {
      clearTimeout(typingTimeout.current);
    }

    if (value.trim() !== "") {
      await setDoc(doc(db, "typing", currentUser.uid), {
        user: currentUser.displayName,
        room,
        timestamp: serverTimestamp(),
      });

      typingTimeout.current = setTimeout(async () => {
        await deleteDoc(doc(db, "typing", currentUser.uid));
      }, 3000);
    } else {
      await deleteDoc(doc(db, "typing", currentUser.uid));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (newMessage.trim() === "") return;

    const currentUser = auth.currentUser;

    await addDoc(messagesRef, {
      text: newMessage,
      createdAt: serverTimestamp(),
      user: currentUser.displayName,
      photoURL: currentUser.photoURL || "",
      uid: currentUser.uid,
      room,
      reactions: {},
    });

    await deleteDoc(doc(db, "typing", currentUser.uid));
    setNewMessage("");
    setShowEmojiPicker(false);
  };

  const handleEmojiClick = (emojiData) => {
    setNewMessage((prev) => prev + emojiData.emoji);
    inputRef.current.focus();
  };

  const getShortName = (fullName) => {
    const parts = fullName.trim().split(" ");
    return parts.length >= 2 ? parts[parts.length - 1] : fullName;
  };

  const handleReaction = async (messageId, emoji) => {
    const currentUser = auth.currentUser;
    const message = messages.find((msg) => msg.id === messageId);

    const currentReactions = message.reactions || {};
    const existingUsers = currentReactions[emoji] || [];

    const hasReacted = existingUsers.includes(currentUser.uid);
    const updatedUsers = hasReacted
      ? existingUsers.filter((id) => id !== currentUser.uid)
      : [...existingUsers, currentUser.uid];

    const updatedReactions = {
      ...currentReactions,
      [emoji]: updatedUsers,
    };

    await updateDoc(doc(db, "messages", messageId), {
      reactions: updatedReactions,
    });
  };

  const renderReactions = (reactions = {}, messageId) => {
    return (
      <div className="reactions">
        {reactionOptions.map((emoji) => {
          const count = reactions[emoji]?.length || 0;
          const reacted = reactions[emoji]?.includes(auth.currentUser.uid);

          return (
            <button
              key={emoji}
              className={`reaction-button ${reacted ? "active" : ""}`}
              onClick={() => handleReaction(messageId, emoji)}
            >
              {emoji} {count > 0 && <span>{count}</span>}
            </button>
          );
        })}
      </div>
    );
  };

  return (
    <div className="chat-app">
      <div className="header">
        <h1>Welcome to: {room.toUpperCase()}</h1>
      </div>

      <div className="messages">
        {messages.map((message) => (
          <div key={message.id} className="message">
            <img
              src={
                message.photoURL && message.photoURL.startsWith("http")
                  ? message.photoURL
                  : "https://via.placeholder.com/35"
              }
              alt="avatar"
              className="avatar"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/35";
              }}
            />
            <div>
              <span className="user">{getShortName(message.user)}:</span>{" "}
              {message.text}
              {renderReactions(message.reactions, message.id)}
            </div>
          </div>
        ))}

        {typingUsers.length > 0 && (
          <div className="typing-indicator">
            {typingUsers.join(", ")} {typingUsers.length > 1 ? "are" : "is"} typing...
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className="new-message-form">
        <div className="input-area">
          <input
            ref={inputRef}
            type="text"
            value={newMessage}
            onChange={(e) => handleTyping(e.target.value)}
            className="new-message-input"
            placeholder="Type your message here..."
          />
          <button
            type="button"
            onClick={() => setShowEmojiPicker((prev) => !prev)}
            className="emoji-button"
          >
            ðŸ˜Š
          </button>
        </div>

        {showEmojiPicker && (
          <div className="emoji-picker">
            <EmojiPicker onEmojiClick={handleEmojiClick} />
          </div>
        )}

        <button type="submit" className="send-button">
          Send
        </button>
      </form>
    </div>
  );
};
