import React, { useState, useEffect, useRef } from "react";
import { db, auth } from "../firebase-config";
import {
  collection,
  addDoc,
  where,
  serverTimestamp,
  onSnapshot,
  query,
  orderBy,
  setDoc,
  doc,
  deleteDoc,
} from "firebase/firestore";

import "../styles/Chat.css";

export const Chat = ({ room }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [typingUsers, setTypingUsers] = useState([]);

  const typingRef = collection(db, "typing");
  const messagesRef = collection(db, "messages");

  const typingTimeout = useRef(null);

  useEffect(() => {
    const queryMessages = query(
      messagesRef,
      where("room", "==", room),
      orderBy("createdAt")
    );

    const unsubscribe = onSnapshot(queryMessages, (snapshot) => {
      const msgs = [];
      snapshot.forEach((doc) => {
        msgs.push({ ...doc.data(), id: doc.id });
      });
      setMessages(msgs);
    });

    // Typing indicator listener
    const typingQuery = query(typingRef, where("room", "==", room));
    const unsubscribeTyping = onSnapshot(typingQuery, (snapshot) => {
      const usersTyping = [];
      snapshot.forEach((doc) => {
        if (doc.id !== auth.currentUser.uid) {
          usersTyping.push(doc.data().user);
        }
      });
      setTypingUsers(usersTyping);
    });

    return () => {
      unsubscribe();
      unsubscribeTyping();
    };
  }, [room]);

  const handleTyping = async (value) => {
    setNewMessage(value);
    const currentUser = auth.currentUser;

    if (typingTimeout.current) {
      clearTimeout(typingTimeout.current);
    }

    if (value.trim() !== "") {
      await setDoc(doc(db, "typing", currentUser.uid), {
        user: currentUser.displayName,
        room,
        timestamp: serverTimestamp(),
      });

      typingTimeout.current = setTimeout(async () => {
        await deleteDoc(doc(db, "typing", currentUser.uid));
      }, 3000);
    } else {
      await deleteDoc(doc(db, "typing", currentUser.uid));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (newMessage.trim() === "") return;

    const currentUser = auth.currentUser;

    await addDoc(messagesRef, {
      text: newMessage,
      createdAt: serverTimestamp(),
      user: currentUser.displayName,
      photoURL: currentUser.photoURL || "",
      uid: currentUser.uid,
      room,
    });

    await deleteDoc(doc(db, "typing", currentUser.uid)); // remove typing status
    setNewMessage("");
  };

  const getShortName = (fullName) => {
    const parts = fullName.trim().split(" ");
    return parts.length >= 2 ? parts[parts.length - 1] : fullName;
  };

  return (
    <div className="chat-app">
      <div className="header">
        <h1>Welcome to: {room.toUpperCase()}</h1>
      </div>

      <div className="messages">
        {messages.map((message) => (
          <div key={message.id} className="message">
            <img
              src={
                message.photoURL && message.photoURL.startsWith("http")
                  ? message.photoURL
                  : "https://via.placeholder.com/35"
              }
              alt="avatar"
              className="avatar"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/35";
              }}
            />
            <span className="user">{getShortName(message.user)}:</span>{" "}
            {message.text}
          </div>
        ))}
        {typingUsers.length > 0 && (
          <div className="typing-indicator">
            {typingUsers.join(", ")} {typingUsers.length > 1 ? "are" : "is"} typing...
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className="new-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => handleTyping(e.target.value)}
          className="new-message-input"
          placeholder="Type your message here..."
        />
        <button type="submit" className="send-button">
          Send
        </button>
      </form>
    </div>
  );
};
