import React, { useState, useEffect } from "react";
import { db, auth, storage } from "../firebase-config";
import {
  collection,
  addDoc,
  where,
  serverTimestamp,
  onSnapshot,
  query,
  orderBy,
} from "firebase/firestore";
import {
  ref,
  uploadBytes,
  getDownloadURL
} from "firebase/storage";

import "../styles/Chat.css";

export const Chat = ({ room }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [imageUpload, setImageUpload] = useState(null); // new image state
  const messagesRef = collection(db, "messages");

  useEffect(() => {
    const queryMessages = query(
      messagesRef,
      where("room", "==", room),
      orderBy("createdAt")
    );

    const unsubscribe = onSnapshot(queryMessages, (snapshot) => {
      const msgs = [];
      snapshot.forEach((doc) => {
        msgs.push({ ...doc.data(), id: doc.id });
      });
      setMessages(msgs);
    });

    return () => unsubscribe();
  }, [room]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (newMessage.trim() === "" && !imageUpload) return;

    const currentUser = auth.currentUser;
    let imageUrl = "";

    // If there's an image, upload it to Firebase Storage
    if (imageUpload) {
      const imageRef = ref(storage, `chatImages/${Date.now()}_${imageUpload.name}`);
      await uploadBytes(imageRef, imageUpload);
      imageUrl = await getDownloadURL(imageRef);
    }

    await addDoc(messagesRef, {
      text: newMessage,
      imageUrl: imageUrl,
      createdAt: serverTimestamp(),
      user: currentUser.displayName,
      photoURL: currentUser.photoURL || "",
      uid: currentUser.uid,
      room,
    });

    setNewMessage("");
    setImageUpload(null); // reset image input
  };

  const getShortName = (fullName) => {
    const parts = fullName.trim().split(" ");
    if (parts.length >= 2) {
      return parts[parts.length - 1];
    } else {
      return fullName;
    }
  };

  return (
    <div className="chat-app">
      <div className="header">
        <h1>Welcome to: {room.toUpperCase()}</h1>
      </div>

      <div className="messages">
        {messages.map((message) => (
          <div key={message.id} className="message">
            <img
              src={
                message.photoURL && message.photoURL.startsWith("http")
                  ? message.photoURL
                  : "https://via.placeholder.com/35"
              }
              alt="avatar"
              className="avatar"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/35";
              }}
            />
            <span className="user">{getShortName(message.user)}:</span>{" "}
            {message.text}
            {message.imageUrl && (
              <div className="message-image">
                <img src={message.imageUrl} alt="uploaded" style={{ maxWidth: "200px", marginTop: "8px" }} />
              </div>
            )}
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="new-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(event) => setNewMessage(event.target.value)}
          className="new-message-input"
          placeholder="Type your message here..."
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImageUpload(e.target.files[0])}
          className="image-upload-input"
        />
        <button type="submit" className="send-button">
          Send
        </button>
      </form>
    </div>
  );
};
